#!/bin/sh

#
# travis-javadoc.sh - A script to build the javadocs of a SciJava-based project.
#

if [ "$TRAVIS_SECURE_ENV_VARS" = true \
  -a "$TRAVIS_PULL_REQUEST" = false \
  -a "$TRAVIS_BRANCH" = master ]
then
  project=$1

  # Build the javadocs.
  mvn -Pbuild-javadoc &&
  test -d target/apidocs &&
  # Strip out date stamps, to avoid spurious changes being committed.
  sed -i'' -e '/\(<!-- Generated by javadoc \|<meta name="date" \)/d' $(find target/apidocs -name '*.html') &&

  # Configure SSH. The file .travis/javadoc.scijava.org.enc must contain
  # an encrypted private RSA key for communicating with the git remote.
  mkdir -p "$HOME/.ssh" &&
  openssl aes-256-cbc -K "$encrypted_cb3727795fd5_key" -iv "$encrypted_cb3727795fd5_iv" -in '.travis/javadoc.scijava.org.enc' -out "$HOME/.ssh/id_rsa" -d &&
  chmod 400 "$HOME/.ssh/id_rsa" &&
  echo 'github.com,192.30.253.113 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.253.112 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.120 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.121 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.122 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.123 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.128 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.129 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.130 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.131 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&
  echo '192.30.252.142 ssh-rsa ' >> "$HOME/.ssh/known_hosts" &&

  # Configure git settings.
  git config --global user.email "travis@travis-ci.org" &&
  git config --global user.name "Travis CI" &&

  # Clone the javadoc.scijava.org repository.
  git clone --quiet --depth 1 git@github.com:scijava/javadoc.scijava.org > /dev/null &&

  # Update the relevant javadocs.
  cd javadoc.scijava.org &&
  rm -rf "$project" &&
  mv ../target/apidocs "$project" &&

  # Commit and push the changes.
  git add "$project" &&
  success=1

  test "$success" || exit 1

  git commit -m "Update $project javadocs (Travis build $TRAVIS_BUILD_NUMBER)"
  git push -q origin gh-pages > /dev/null || exit 2

  echo "Update complete."
else
  echo "Skipping non-canonical branch $TRAVIS_BRANCH"
fi
